INSERT INTO `rpg_quest` (`id`, `about_ge`, `about_eng`, `answer_ge`, `answer_eng`, `waittext_ge`, `waittext_eng`, `anchor_ge`, `anchor_eng`, `gift`, `money`, `fbot`, `lvl`, `step`) VALUES
(1, 'Mogesalmebi axalbeda meomaro, rogorc vxedav jer ar gacnobilxar am samyaros. \r\naq shen gelodeba mudmivi brdzolebi da tavgadasavlebi. manamde ki shegidzlia sheni dzalebi chemtan gamocado shemebrdzole da tu momigeb chemgan gasamrjelosac miigeb', 'My Dear New Warrior!\r\nSeems like you have not got acquainted to this world. Constant battles and adventures are awaiting for your here. Before that, you can test your skills with me, fight me and if you win you will even get some reward from me.', 'Yochag shen damimtkice sheni sidzliere. gaitvaliswine sheni sidzliere damokidebulia shens parametrebze parametrebis gazrda ki shegidzlia tavad tu kargi iaragebi geqneba aige chemgan es xmali. ', 'Well Done! You have proved to me your strength. Beware, that your strength depends on your stats. You can improve your stats yourself if you will get good weapons! Now, take this sword!', NULL, NULL, 'Shevebrdzolot', 'Fight', 4, 0, 18, 0, 0),
(2, 'Daimaxsovre sheni sidzliere damokidebulia shens parametrebze, parametrebis shesaxeb shegidzlia waikitxo qvevit daxmarebashi. axla ki shedi chantashi shemdeg iaragebshi da chaicvi chemi mocemuli xmali.', 'Remember your strength depends on stats, you can read about the stats below in Game Help. Now proceed to the BAG, then to weapons and take the sword.', 'yochag warmatebiit gaartvi davalebas tavi miige chemgan cotaodeni oqro. oqro chven samyaroshi aris fuli romlitac titiqmis yvelafris yidva shegidzlia magaziashi, magram iqneb manamde sheni axali xmlis gamocda ginda?', 'Good Job! You have successfully completed the task. Receive some gold from me. Gold is the money in our world, with which you can buy anything in the shop, but maybe before you would like to try out your new sword?', 'Shen kidev ar shegisrulebia chemi davaleba ubralod unda shexvide chantashi shemdeg iaragebshi da chaicva', 'You have not completed given task yet! Simply go to the Bag, then to weapons and put it on', 'Me amas gavaketeb', 'Get quest', 0, 2, 0, 0, 0),
(3, 'gamocade sheni axali xmali da sheebrdzole cxovels romelic karis ukan imaleba', 'Try your new sword out and fight the animal which is hiding behind the door', 'Yochag shen martivad gaartvi tavi am davalebas. daimaxsovre cxovelebtan brdzolisas shen igeb gamocdilebas EXP-s ritac dones imateb da OQRO-s ritac shegidzlia magaziashi nivtebi sheidzino ', 'Well Done! You have easily completed this task. Remember, while fighting the animals you are getting more experience EXP, which will let you to proceed to next levels and you get Gold which you can use to buy things in the store.', 'Samwuxarod shen kidev ver sheasrule chemi davaleba', 'You have not completed given task yet! ', 'Karis gageba', 'Open the door', 0, 1, 1, 0, 0),
(4, 'Axla droa sakutari dzalebi gamocado sxva gmirebtan wadi galaqshi shedi duelebshi ,  modzebne mowinaagmdege da sheebrdzole tu gaimarjveb dabrundi chemtan da chemgan jildosac miigeb', 'now it is time to test your strength with other characters. go to the town, participate in duels, find an opponent and fight! If you win, return to me and you will get a reward!', 'Yochag vxedav sheni shesadzleblobebi izrdeba miige chemgan jildo', 'Well Done! I see you skills are increasing so receive a reward from me', 'Daimaxsovre unda moxvde qalaqshi da gaimarjvo duelshi', 'Remember, you need to go to the town and win the duels', 'Davalebis ageba', 'Take quest', 0, 2, 0, 0, 0),
(5, 'Axla ki droa gadaxvide shemdeg levelze rata gagexsnas bevri shesadzleblobebi. amisatvis gaagrdzele duelebshi brdzola da gadadi 1 levelze', 'Now it is time to proceed to next level, in order to open new opportunities. For this you need to continue to fight in duels and proceed to Level 1', 'gilocav shen ukve 1 leveli xar ar dagaviwydes moimato parametrebi amisatvis sachiroa shexvide shens saxelshi zemot. ', 'Congratulations, you are on level 1. Do not forget to add stats, for this you need to go to Home above', 'Shen isev 0 leveli xar dabrundi roca 1 gaxdebi', 'You are still on Level 0, come back when you are on Level 1', 'Kargi', 'OK', 0, 2, 0, 0, 0);
